    List<TodoItem> todos = new List<TodoItem>();
    private string newTodo;

    private void AddTodo()
    {
        if (!string.IsNullOrWhiteSpace(newTodo))
        {
            TodoItem todo = new TodoItem()
            {
                isDone = false,
                Title = newTodo,
                AddedIn = DateTime.Now,
            };
